                $scope.imageSelectModal = {};
                var searchQuery = '';

                if ($scope.model.title) {
                    searchQuery = searchQuery + ' ' + $scope.model.title;
                }
                if ($scope.model.isbn) {
                    searchQuery = searchQuery + ' ' + $scope.model.isbn;
                }
                if ($scope.data.selectedAuthor) {
                    searchQuery = searchQuery + ' ' + $scope.data.selectedAuthor.name.lastname;
                }

